# 用户
DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `id` int(32) NOT NULL AUTO_INCREMENT COMMENT '数据库自增ID',
  `gmt_create` datetime NOT NULL DEFAULT '1970-01-01 00:00:01' COMMENT '数据创建时间',
  `gmt_modify` datetime NOT NULL DEFAULT '1970-01-01 00:00:01' COMMENT '数据修改时间',
  `account` varchar(32) NOT NULL COMMENT '用户登录账号, 一般是手机号',
  `password` varchar(32) NOT NULL DEFAULT 'e10adc3949ba59abbe56e057f20f883e' COMMENT '用户登录密码，32位MD5加密',
  `name` varchar(32) NOT NULL COMMENT '用户真实姓名',
  `identity_card` varchar(32) DEFAULT NULL COMMENT '用户身份证',
  `nickname` varchar(32) DEFAULT NULL COMMENT '用户昵称',
  `sex` char(1) NOT NULL DEFAULT 'M' COMMENT '用户性别: M男性、F女性',
  `mobile` varchar(16) NOT NULL COMMENT '用户联系手机号',
  `avatar_url` varchar(512) DEFAULT NULL COMMENT '用户头像URL地址',
  `address` varchar(256) DEFAULT NULL COMMENT '用户居住地址',
  `email` varchar(256) DEFAULT NULL COMMENT '用户电子邮箱地址',
  `emergency_contact` varchar(32) DEFAULT NULL COMMENT '紧急联系人姓名',
  `emergency_mobile` varchar(16) DEFAULT NULL COMMENT '紧急联系人电话',
  `last_login_time` datetime NOT NULL DEFAULT '1970-01-01 00:00:01' COMMENT '上次登录时间',
  `last_login_ip` varchar(32) NOT NULL DEFAULT '0.0.0.0' COMMENT '上次登录IP',
  `category_id` int(11) NOT NULL DEFAULT '7' COMMENT '类目ID: 绿化、清洁、工程维修、安保、电梯、服务中心、其他、邻里、室内维修，现在使用的是admin_services_type表的数据',
  `category_name` varchar(32) NOT NULL DEFAULT '其他' COMMENT '类目名称',
  `company_id` int(32) NOT NULL COMMENT '所在公司ID, 对应admin_services表',
  `zone_id` int(32) NOT NULL COMMENT '所在小区ID',
  `zone_ids` varchar(128) NOT NULL DEFAULT '' COMMENT '所在的多个小区ID组成的的字符串，中间用逗号隔开，如果只有一个小区，则就是zone_id的值',
  `is_on_job` char(1) NOT NULL DEFAULT '1' COMMENT '是否在职：在职1 离职0',
  `departure_time` datetime NOT NULL DEFAULT '1970-01-01 00:00:01' COMMENT '离职时间',
  `job_title_id` int(11) NOT NULL DEFAULT '0' COMMENT '头衔ID',
  `job_title_name` varchar(32) NOT NULL DEFAULT '' COMMENT '头衔名称',
  `old_user_id` int(11) DEFAULT NULL COMMENT '老的admin_employee的ID',
  `verification_code` varchar(10) DEFAULT NULL COMMENT '密码注册重置验证码',
  `work_status` tinyint(4) NOT NULL DEFAULT '0' COMMENT '上下班状态：1:上班中 0:下班中',
  `check_in_time` smallint(6) DEFAULT NULL COMMENT '上班时间 0830',
  `check_out_time` smallint(6) DEFAULT NULL COMMENT '下班时间 2290',
  `shift_plan` varchar(6) DEFAULT NULL COMMENT '倒班，例如，白班、晚班',
  PRIMARY KEY (`id`),
  UNIQUE KEY `uk_account` (`account`)
) ENGINE=InnoDB AUTO_INCREMENT=1074 DEFAULT CHARSET=utf8mb4 COMMENT='用户表 user';

