# 老小区的任务相关数据
DROP TABLE IF EXISTS `itl_zone_task`;
CREATE TABLE `itl_zone_task` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '数据库自增ID',
  `created_time` datetime NOT NULL DEFAULT '1970-01-01 00:00:00' COMMENT '数据创建时间',
  `modified_time` datetime NOT NULL DEFAULT '1970-01-01 00:00:00' COMMENT '数据修改时间',
  `zone_id` int(11) NOT NULL COMMENT '小区ID',
  `code` varchar(16) NOT NULL COMMENT '任务编号：Z1AD000000000001，规则: Z + 1A/1B/1C/1D/1E(任务类型) + D/W/M/Y(重复频率) + 序列(12位)',
  `type` char(2) NOT NULL COMMENT '任务类型',
  `content` varchar(256) NOT NULL COMMENT '任务内容',
  `standard` varchar(512) NOT NULL DEFAULT '' COMMENT '任务标准',
  `rate` char(1) NOT NULL DEFAULT 'D' COMMENT '重复频率：D每天、W每周、M每月、Y每年',
  `start_time` smallint(6) NOT NULL DEFAULT '1' COMMENT '开始时间(不是具体时间)：根据重复频率: 每天：-1；每周：1-7；每月：1-31；每年：1231',
  `overdue_time` smallint(6) NOT NULL DEFAULT '1' COMMENT '过期时间(不是具体时间)：根据重复频率: 每天：-1；每周：1-7；每月：1-31；每年：1231',
  `role_code` char(128) NOT NULL COMMENT '职能编码',
  `role_name` char(128) NOT NULL COMMENT '职能名称，冗余方便查询展示',
  `sort_num` smallint(6) NOT NULL DEFAULT '1' COMMENT '排序值，可用于展示等',
  `is_valid` tinyint(4) NOT NULL DEFAULT '1' COMMENT '该任务是否有效：1有效、2无效。无效的任务不会被使用。',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3535 DEFAULT CHARSET=utf8mb4 COMMENT='由爱家田螺整个任务池的任务模板+项目自定义任务，生成的各项目自己的任务库';

