# 小区
DROP TABLE IF EXISTS `zones`;
CREATE TABLE `zones` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(30) NOT NULL COMMENT '小区名称',
  `city` varchar(30) NOT NULL DEFAULT '0' COMMENT '城市',
  `area` varchar(30) NOT NULL DEFAULT '0' COMMENT '地区',
  `description` varchar(255) NOT NULL COMMENT '小区简介',
  `buildtime` varchar(20) NOT NULL COMMENT '建造时间',
  `total_area` varchar(10) NOT NULL COMMENT '总面积',
  `houses` int(11) NOT NULL COMMENT '总户数',
  `address` varchar(120) NOT NULL COMMENT '地址',
  `service` varchar(20) NOT NULL COMMENT '物业服务电话',
  `helpme` varchar(20) NOT NULL COMMENT '紧急报警电话',
  `seal_icon` varchar(500) NOT NULL COMMENT '小区印章',
  `funds` decimal(10,2) NOT NULL COMMENT '小区账户余额',
  `property_fee` decimal(10,2) NOT NULL COMMENT '每平方物业费',
  `cars_fee` decimal(10,2) NOT NULL COMMENT '每月车位费',
  `cars_year` decimal(6,2) NOT NULL DEFAULT '0.00' COMMENT '固定车位年费',
  `car_rent` decimal(6,2) NOT NULL DEFAULT '0.00' COMMENT '临时车位月费',
  `rent_year` decimal(6,2) NOT NULL DEFAULT '0.00' COMMENT '临时车位年费',
  `energy` decimal(10,2) NOT NULL COMMENT '每月能耗费',
  `buildings` tinyint(3) NOT NULL DEFAULT '0' COMMENT '最多多少幢',
  `units` tinyint(2) NOT NULL DEFAULT '0' COMMENT '一幢最多单元数',
  `floors` tinyint(2) NOT NULL DEFAULT '0' COMMENT '最高多少层',
  `doors` tinyint(2) NOT NULL DEFAULT '0' COMMENT '一个单元最多户数',
  `status` tinyint(2) NOT NULL DEFAULT '0' COMMENT '是否入住',
  `online` varchar(4) NOT NULL DEFAULT '0800' COMMENT '物业上班时间',
  `offline` varchar(4) NOT NULL DEFAULT '2200' COMMENT '物业下班时间',
  `has_feed` tinyint(2) NOT NULL DEFAULT '0' COMMENT '是否开放朋友圈',
  `enter_time` int(6) NOT NULL COMMENT '入驻时间',
  `off_time` int(6) NOT NULL DEFAULT '0' COMMENT '合约到期时间',
  `cars_num` int(11) NOT NULL COMMENT '车位数量',
  `perm_house` int(11) NOT NULL COMMENT '常住业主',
  `rent_house` int(11) NOT NULL COMMENT '出租户数',
  `shop_house` int(11) NOT NULL COMMENT '商铺户数',
  `leave_house` int(11) NOT NULL COMMENT '闲置户数',
  `last_year_amount` int(11) NOT NULL DEFAULT '0' COMMENT '去年小区结余',
  `total_out_amount` int(11) NOT NULL DEFAULT '0' COMMENT '今年总支出',
  `last_year_owe` int(11) NOT NULL DEFAULT '0' COMMENT '去年欠费金额',
  `payroll_num` int(11) NOT NULL DEFAULT '0' COMMENT '项目编制岗位人数',
  `lngandlat` varchar(100) NOT NULL DEFAULT '0' COMMENT '经纬度',
  `company_id` int(11) NOT NULL COMMENT 'itl_company表id',
  `province` varchar(20) DEFAULT NULL COMMENT '省份',
  `info` text COMMENT 'son存储额外信息，包含鸟瞰图、正门图、其他备注',
  `type` varchar(20) DEFAULT NULL COMMENT '项目类型，住宅、写字楼、商业体、其他公建',
  `subtype` varchar(20) DEFAULT NULL COMMENT '住宅类型：高层、小高层、多层、别墅、排屋',
  `manager_id` int(11) NOT NULL DEFAULT '0' COMMENT '小区的项目管理员',
  `paid_in_order` tinyint(4) NOT NULL DEFAULT '0' COMMENT '该小区的账单是否顺序付款，1必须顺序付款，0可以任意付款',
  PRIMARY KEY (`id`),
  KEY `name` (`name`),
  KEY `service` (`service`),
  KEY `helpme` (`helpme`),
  KEY `company` (`company_id`)
) ENGINE=InnoDB AUTO_INCREMENT=36 DEFAULT CHARSET=utf8mb4 COMMENT='小区表';

