# 抽检配置
DROP TABLE IF EXISTS `itl_sampling_config`;
CREATE TABLE `itl_sampling_config` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '数据库自增ID',
  `gmt_create` datetime NOT NULL DEFAULT '1970-01-01 00:00:01' COMMENT '数据创建时间',
  `gmt_modify` datetime NOT NULL DEFAULT '1970-01-01 00:00:01' COMMENT '数据修改时间',
  `user_id` int(11) NOT NULL COMMENT '用户ID，抽检人',
  `zone_id` int(11) NOT NULL COMMENT '小区ID',
  `zone_category_ids` varchar(256) NOT NULL COMMENT '小区分类ID，指的是这个用户他抽检哪些个部门，用英文逗号间隔',
  `sampling_count` smallint(6) NOT NULL COMMENT '每次抽检条数',
  PRIMARY KEY (`id`),
  UNIQUE KEY `uk_user` (`user_id`)
) ENGINE=InnoDB AUTO_INCREMENT=23 DEFAULT CHARSET=utf8mb4 COMMENT='抽检配置表';

